import React from 'react';

function Inicio() {
  return (
    <div style={styles.container}>
      <header style={styles.header}>
        <h1 style={styles.title}>Bienvenido a Mi Aplicaci√≥n</h1>
        <p style={styles.subtitle}>
          Explora las funcionalidades, gestiona tus recursos y mejora tu productividad.
        </p>
        <button style={styles.button}>Comenzar</button>
      </header>
    </div>
  );
}

const styles = {
  container: {
    height: '100vh',
    background: 'linear-gradient(135deg, #1e1e1e, #3a3a3a)',
    color: 'white',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    padding: '0 20px',
  },
  header: {
    textAlign: 'center',
    maxWidth: '600px',
  },
  title: {
    fontSize: '3rem',
    marginBottom: '20px',
  },
  subtitle: {
    fontSize: '1.2rem',
    marginBottom: '30px',
    color: '#ccc',
  },
  button: {
    padding: '12px 24px',
    fontSize: '1rem',
    backgroundColor: '#ff5c5c',
    border: 'none',
    borderRadius: '8px',
    cursor: 'pointer',
    transition: 'background 0.3s ease',
  }
};

export default Inicio;
