import React, { useState } from 'react';

function Menu() {
  const navItems = ['Inicio', 'Servicios', 'Contacto'];

  return (
    <nav style={styles.nav}>
      <div style={styles.container}>
        <div style={styles.logo}>MiAplicaci√≥n</div>
        <ul style={styles.navLinks}>
          {navItems.map((item) => (
            <NavItem key={item} label={item} />
          ))}
        </ul>
      </div>
    </nav>
  );
}

function NavItem({ label }) {
  const [hover, setHover] = useState(false);

  return (
    <li style={{ height: '100%' }}>
      <a
        href={`#${label.toLowerCase()}`}
        style={{
          ...styles.link,
          color: hover ? '#ffffff' : '#e0e0e0',
          background: hover ? 'rgba(255, 255, 255, 0.1)' : 'transparent',
        }}
        onMouseEnter={() => setHover(true)}
        onMouseLeave={() => setHover(false)}
      >
        {label}
      </a>
    </li>
  );
}

const styles = {
  nav: {
    position: 'sticky',
    top: 0,
    width: '100%',
    backgroundColor: '#1a1a1a',
    zIndex: 1000,
    boxShadow: '0 2px 4px rgba(255, 255, 255, 0.05)',
    height: '60px', // Fijamos altura para el nav
  },
  container: {
    maxWidth: '1200px',
    margin: '0 auto',
    padding: '0 16px', // quitamos padding vertical para que el alto sea fijo
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    height: '100%', // para que el contenedor llene el nav
  },
  logo: {
    color: '#ffffff',
    fontSize: '1.5rem',
    fontWeight: 'bold',
  },
  navLinks: {
    listStyle: 'none',
    display: 'flex',
    gap: '24px',
    margin: 0,
    padding: 0,
    height: '100%', // para que ul llene verticalmente
    alignItems: 'center',
  },
  link: {
    display: 'flex', // para centrar verticalmente
    alignItems: 'center',
    height: '8%', // que ocupe todo el alto del nav
    padding: '0 12px', // algo de espacio horizontal
    textDecoration: 'none',
    fontSize: '1rem',
    transition: 'color 0.3s ease, background 0.3s ease',
    borderRadius: '4px', // bordes redondeados para mejor look
    cursor: 'pointer',
  },
};

export default Menu;
