import React from 'react';

function Servicios() {
  const services = [
    {
      title: 'Desarrollo Web',
      description: 'Creamos sitios web modernos, responsivos y optimizados para todos los dispositivos.',
    },
    {
      title: 'Aplicaciones Móviles',
      description: 'Desarrollamos apps para Android y iOS con tecnología nativa o híbrida.',
    },
    {
      title: 'Consultoría Tecnológica',
      description: 'Asesoramos a empresas en transformación digital y mejora de procesos tecnológicos.',
    },
  ];

  return (
    <div style={styles.container}>
      <h1 style={styles.heading}>Nuestros Servicios</h1>
      <div style={styles.grid}>
        {services.map((service, index) => (
          <div key={index} style={styles.card}>
            <h2 style={styles.title}>{service.title}</h2>
            <p style={styles.description}>{service.description}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

const styles = {
  container: {
    padding: '40px 20px',
    backgroundColor: '#f5f5f5',
    minHeight: 'calc(100vh - 60px)',
  },
  heading: {
    textAlign: 'center',
    fontSize: '2.5rem',
    marginBottom: '40px',
    color: '#333',
  },
  grid: {
    display: 'flex',
    flexWrap: 'wrap',
    justifyContent: 'center',
    gap: '30px',
  },
  card: {
    backgroundColor: '#fff',
    padding: '24px',
    borderRadius: '12px',
    boxShadow: '0 4px 12px rgba(0, 0, 0, 0.1)',
    maxWidth: '300px',
    transition: 'transform 0.3s',
  },
  title: {
    fontSize: '1.25rem',
    marginBottom: '12px',
    color: '#1a1a1a',
  },
  description: {
    fontSize: '1rem',
    color: '#555',
  },
};

export default Servicios;
