import React, { useState } from 'react';
import '../styles/inicio.css';

const imagenes = [
  {
    src: '/img/carrusel1.jpg',
    texto: 'Pasteles Artesanales',
  },
  {
    src: '/img/carrusel2.jpg',
    texto: 'Sabores Únicos',
  },
  {
    src: '/img/carrusel3.jpg',
    texto: 'Endulza tu Día',
  },
];

function Inicio() {
  const [indice, setIndice] = useState(0);

  const siguiente = () => {
    setIndice((indice + 1) % imagenes.length);
  };

  const anterior = () => {
    setIndice((indice - 1 + imagenes.length) % imagenes.length);
  };

  const seleccionar = (i) => {
    setIndice(i);
  };

  return (
    <div className="carrusel">
      <img
        src={imagenes[indice].src}
        alt={`slide-${indice}`}
        className="carrusel-img"
      />
      <div className="carrusel-texto">
        <h2>{imagenes[indice].texto}</h2>
      </div>
      <button className="carrusel-btn izq" onClick={anterior}>‹</button>
      <button className="carrusel-btn der" onClick={siguiente}>›</button>

      <div className="carrusel-indicadores">
        {imagenes.map((_, i) => (
          <span
            key={i}
            className={`punto ${i === indice ? 'activo' : ''}`}
            onClick={() => seleccionar(i)}
          ></span>
        ))}
      </div>
    </div>
  );
}

export default Inicio;
