import React, { useState } from 'react';
import { Link } from 'react-router-dom';

function Menu() {
  const navItems = [
    { label: 'Inicio', path: '/' },
    { label: 'Servicios', path: '/servicios' },
    { label: 'Contacto', path: '/contacto' },
  ];

  return (
    <nav style={styles.nav}>
      <div style={styles.container}>
        <div style={styles.logo}>MiAplicaci√≥n</div>
        <ul style={styles.navLinks}>
          {navItems.map((item) => (
            <NavItem key={item.label} label={item.label} path={item.path} />
          ))}
        </ul>
      </div>
    </nav>
  );
}

function NavItem({ label, path }) {
  const [hover, setHover] = useState(false);

  return (
    <li style={{ height: '100%' }}>
      <Link
        to={path}
        style={{
          ...styles.link,
          color: hover ? '#ffffff' : '#e0e0e0',
          background: hover ? 'rgba(255, 255, 255, 0.1)' : 'transparent',
        }}
        onMouseEnter={() => setHover(true)}
        onMouseLeave={() => setHover(false)}
      >
        {label}
      </Link>
    </li>
  );
}

const styles = {
  nav: {
    position: 'sticky',
    top: 0,
    width: '100%',
    backgroundColor: '#1a1a1a',
    zIndex: 1000,
    boxShadow: '0 2px 4px rgba(255, 255, 255, 0.05)',
    height: '60px',
  },
  container: {
    maxWidth: '1200px',
    margin: '0 auto',
    padding: '0 16px',
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    height: '100%',
  },
  logo: {
    color: '#ffffff',
    fontSize: '1.5rem',
    fontWeight: 'bold',
  },
  navLinks: {
    listStyle: 'none',
    display: 'flex',
    gap: '24px',
    margin: 0,
    padding: 0,
    height: '100%',
    alignItems: 'center',
  },
  link: {
    display: 'flex',
    alignItems: 'center',
    height: '100%',
    padding: '0 12px',
    textDecoration: 'none',
    fontSize: '1rem',
    transition: 'color 0.3s ease, background 0.3s ease',
    borderRadius: '4px',
    cursor: 'pointer',
  },
};

export default Menu;
